definition.script:
node {
    stage ('build') {
        git  '{insert-git-url}'
        sh './gradlew clean packDeb'
    }

    stage ('publish to aptly') {
        sh 'aptly repo add -force-replace {insert-repository-name} build/distributions/*.deb'
        sh 'aptly publish update -force-overwrite -skip-signing=true trusty'
    }

    stage ('archive') {
        archiveArtifacts 'build/distributions/*.deb'
    }
}
